generated.css:
* {
  padding: 0;
  margin: 0;
  box-sizing: border-box;
}
.icon {
  width: 1em;
  height: 1em;
  vertical-align: -0.15em;
  fill: currentColor;
  overflow: hidden;
}
a {
  color: inherit;
  text-decoration: none;
}
.contain {
  display: flex;
}
body {
  background: rgb(242, 242, 242);
}
.left {
  width: 20%;
}
.right {
  flex-grow: 8;
}
/* 搜索框样式 */
.search {
  padding: 60px 0;
}
form {
  position: relative;
  width: 700px;
  margin: 0 auto;
}
input,
button {
  border: none;
  outline: none;
}
input {
  width: 100%;
  height: 42px;
  padding-left: 13px;
  padding-right: 46px;
}
button {
  height: 42px;
  width: 42px;
  cursor: pointer;
  position: absolute;
}

.bar1 input {
  border: 2px solid #7ba7ab;
  border-radius: 9px;
}
.bar1 button {
  top: 0;
  right: 0;
  background: #7ba7ab;
  border-radius: 0 5px 5px 0;
}
.bar1 button:before {
  content: "\f002";
  font-family: FontAwesome;
  font-size: 16px;
  color: #f9f0da;
}

.content {
  border-radius: 5px;
  width: 90%;
  margin: 0 auto;

  margin-top: 20px;
}
.black {
  margin-top: 8px;
  display: flex;
  flex-wrap: wrap;
  margin-right: -13px;
}
.item {
  margin-left: 8px;
}
.little {
  background: white;
  width: 19%;
  margin-right: 12px;
  display: flex;
  flex-direction: column;
  align-items: center;
  margin-top: 12px;
  border-radius: 5px;
  position: relative;
}
.little:hover {
  background: #7ba7ab;
  cursor: pointer;
  color: white;
}
.little .logo {
  font-size: 80px;
}
.little .url {
  padding: 0 0 10px 0;
}
.little .close {
  position: absolute;
  top: 1px;
  right: 10px;
  cursor: default;
  display: none;
  font-size: 20px;
}
.little:hover > .close {
  display: block;
}
.item2 {
  margin-top: 25px;
}
.little1 {
  background: rbg(158, 195, 230);
  border: 1px solid #ddd;
  height: 150px;
  width: 19%;
  margin: 1px 6px 12px 5px;
}

.little2 {
  height: 100%;
}
.title {
  margin: 10px 2px 5px 2px;
}

@media (max-width: 500px) {
  .left {
    display: none;
  }
  form {
    width: 90%;
  }
  .black {
    justify-content: space-between;
  }
  .content {
    width: 90%;
    margin-top: -30px;
    margin-bottom: 60px;
  }
  .little {
    width: 46%;
  }
  .little1 {
    width: 46%;
  }
  .little .close {
    display: block;
  }
}

ol {
  list-style-type: none;
}
.left {
  background: white;
  height: 720px;
}
.left li {
  padding: 10px 40px;
}
.left li:hover {
  background: #7ba7ab;
}
.left li {
  display: flex;
  align-items: center;
}

.col-black {
  display: flex;
  flex-wrap: wrap;
  margin-right: -20px;
}
.col-black .col-little {
  padding: 10px 20px;
  width: 199px;
  margin-right: 5px;
  margin-top: 20px;
  background: white;
  border-radius: 5px;
  display: flex;
  justify-content: center;
}
.col-black .col-little:hover {
  background: #7ba7ab;
  cursor: pointer;
}
@media (max-width: 500px) {
  .col-black {
    justify-content: space-between;
  }
  .col-black .col-little {
    width: 180px;
  }
}
