generated.css:
* {
  padding: 0;
  margin: 0;
  box-sizing: border-box;
}
.icon {
  width: 1em;
  height: 1em;
  vertical-align: -0.15em;
  fill: currentColor;
  overflow: hidden;
}
a {
  color: inherit;
  text-decoration: none;
}
.contain {
  display: flex;

}

body {
  background: rgb(242, 242, 242);
}
.positions{
    position: -webkit-sticky;
    position: sticky;
    top: 20px;
}
.right {
  flex-grow: 8;
}
/* 搜索框样式 */
.search {
  padding: 60px 0;
}
form {
  position: relative;
  width: 70%;
  margin: 0 auto;
}
input,
button {
  border: none;
  outline: none;
}
input {
  width: 100%;
  height: 42px;
  padding-left: 13px;
  padding-right: 46px;
}
button {
  height: 42px;
  width: 42px;
  cursor: pointer;
  position: absolute;
}

.bar1 input {
  border: 2px solid #7ba7ab;
  border-radius: 9px;
}
.bar1 button {
  top: 0;
  right: 0;
  background: #7ba7ab;
  border-radius: 0 5px 5px 0;
}
.bar1 button:before {
  content: "\f002";
  font-family: FontAwesome;
  font-size: 16px;
  color: #f9f0da;
}

.content {
  border-radius: 5px;
  width: 90%;
  margin: 0 auto;
  background: white;
  margin-top: 20px;
  padding: 10px 0;
}
.black {
  margin-top: 8px;
  display: flex;
  flex-wrap: wrap;
  margin-right: -13px;
}
.item {
  margin-left: 8px;
}
.little {
  background: white;
  width: 20%;
  margin: 12px;
  display: flex;
  flex-direction: column;
  align-items: center;
  margin-top: 12px;
  border-radius: 5px;
  position: relative;
  border: 1px solid #e4ecf3;
  box-shadow: 1px 2px 3px #f2f6f8;
}
.little:hover {
  background: #7ba7ab;
  cursor: pointer;
  color: white;
}

@media (min-width: 768px){
    .little {
        width:  17%;
    }
}



.little .logo {
  font-size: 4em;
}
.little .url {
  padding: 0 0 10px 0;
}
.little .close {
  position: absolute;
  top: 1px;
  right: 10px;
  cursor: default;
  display: none;
  font-size: 1em;
}
.little:hover > .close {
  display: block;
}
.item2 {
  margin-top: 25px;
}
.little1 {
  background: rbg(158, 195, 230);
  border: 1px solid #ddd;
  height: 150px;
  width: 19%;
  margin: 1px 6px 12px 5px;

}

.little2 {
  height: 100%;
}
.title {
  margin: 10px 2px 5px 2px;
}

@media (max-width: 500px) {
  .left {
    display: none;
  }
  form {
    width: 90%;
  }
  .black {
    margin: 0;
    justify-content: space-between;
  }
  .content {
    width: 90%;
    margin-top: -30px;
    margin-bottom: 60px;
  }
  .little {
    width: 45%;
    margin: 2%;

  }
  .little1 {
    width: 46%;
  }
  .little .close {
    display: block;
  }
}

ol {
  list-style-type: none;
}
.left {
    width: 30%;
    background: #30333c;
    height: 100vm;
    color:white;
  }

.left li {
  padding: 10px 30px;
}
.left li:hover {
  background: #7ba7ab;
}
.left li {
  display: flex;
  align-items: center;
}
.left .icon{
    margin-right: 7px;
}
.left .mulu{
   font-size: 2em;
   display: flex;
   justify-content: center;
}
.col-black {
  display: flex;
  flex-wrap: wrap;
  margin-right: -20px;

}
.col-black .col-little {
  padding: 10px 20px;
  width: 199px;
  margin: 5px;
  margin-top: 20px;
  background: white;
  border-radius: 5px;
  display: flex;
  justify-content: center;
  border: 1px solid #e4ecf3;
}
.col-black .col-little:hover {
  background: #7ba7ab;
  cursor: pointer;
}
@media (max-width: 500px) {
  .col-black {
    justify-content: space-between;
    margin-right: 0;
  }
  .col-black .col-little {
    width: 170px;
    margin-right: 0;
  }
}

@media (min-width: 481px){
    .right{
        max-width: 1200px;
        margin: 0 auto;
    }
    .left{
        max-width: 30%;
    }
}
