*{padding: 0;margin: 0;box-sizing: border-box;}
a {color: inherit; text-decoration: none;}
.contain{
    display: flex;
    border:1px solid red;
}
.left{  
    border:1px solid red;
    flex-grow: 1.2;
}
.right{
    background:rgb(242,242,242);
    border:1px solid;  
    flex-grow: 8;

}
/* 搜索框样式 */
.search {
    padding: 60px 0;
}
form {
    position: relative;
    width: 700px;
    margin: 0 auto;
}
input, button {
    border: none;
    outline: none;
}
input {
    width: 100%;
    height: 42px;
    padding-left: 13px;
    padding-right:46px;
}
button {
    height: 42px;
    width: 42px;
    cursor: pointer;
    position: absolute;
}

.bar1 input {
    border: 2px solid #7BA7AB;
    border-radius: 9px;
}
.bar1 button {
    top: 0;
    right: 0;
    background: #7BA7AB;
    border-radius: 0 5px 5px 0;
}
.bar1 button:before {
    content: "\f002";
    font-family: FontAwesome;
    font-size: 16px;
    color: #F9F0DA;
}

.content{ 
    width: 90%;
    margin: 0 auto;
    background: white;
    margin-top: 20px;
    border:1px solid; 
}
.black{    
    margin-top: 8px;
    display: flex;
    flex-wrap: wrap;
    border:1px solid; 
} 
.item{ 
    margin-left: 8px;
}
.little{
    border:1px solid #ddd; 
    height: 200px;
    width: 19%;
    margin:1px 6px 12px 5px;
}
.item2{
    margin-top: 25px;
} 
.little1{
    background:rbg(158,195,230);
    border:1px solid #ddd; 
    height: 150px;
    width: 19%;
    margin:1px 6px 12px 5px;
}
.little1:hover{
    background: #7BA7AB;
    color: white;
}
.little2{
    height: 100%;

}
.title{
    margin:10px 2px 5px 2px;

}

@media(max-width:500px){
	.left{
        display: none;
    }
    form{
        width: 90%;
    }
    .content{
        width: 90%;
        border-bottom: 30px;
    }
    .little{
        width: 46%;
    }
    .little1{
        width: 46%;
    }
}
